package com.shekhargulati.adventofcode_2015

import org.scalatest.{FunSpec, Matchers}

class Puzzle1Test extends FunSpec with Matchers {

  describe("Puzzle 1_1 Spec") {
    it("should return to ground floor when input is (())") {
      val floor = Puzzle1.floor("(())")
      floor should be(0)
    }

    it("should return to 3rd floor when input is (((") {
      val floor = Puzzle1.floor("(((")
      floor should be(3)
    }

    it("should return to 3rd floor when input is (()(()(") {
      val floor = Puzzle1.floor("(()(()(")
      floor should be(3)
    }

    it("should return to third when input is ))(((((") {
      val floor = Puzzle1.floor("))(((((")
      floor should be(3)
    }

    it("should return to basement when input is ())") {
      val floor = Puzzle1.floor("())")
      floor should be(-1)
    }

    it("should return to basement when input is ))(") {
      val floor = Puzzle1.floor("))(")
      floor should be(-1)
    }

    it("should return to -3 floor when input is )))") {
      val floor = Puzzle1.floor(")))")
      floor should be(-3)
    }

    it("should return to -3 floor when input is ))(") {
      val floor = Puzzle1.floor(")())())")
      floor should be(-3)
    }
  }

  describe("Puzzle 1_1 Spec Recursive") {
    it("should return to ground floor when input is (())") {
      val floor = Puzzle1.floorR("(())")
      floor should be(0)
    }

    it("should return to 3rd floor when input is (((") {
      val floor = Puzzle1.floorR("(((")
      floor should be(3)
    }

    it("should return to 3rd floor when input is (()(()(") {
      val floor = Puzzle1.floorR("(()(()(")
      floor should be(3)
    }

    it("should return to third when input is ))(((((") {
      val floor = Puzzle1.floorR("))(((((")
      floor should be(3)
    }

    it("should return to basement when input is ())") {
      val floor = Puzzle1.floorR("())")
      floor should be(-1)
    }

    it("should return to basement when input is ))(") {
      val floor = Puzzle1.floorR("))(")
      floor should be(-1)
    }

    it("should return to -3 floor when input is )))") {
      val floor = Puzzle1.floorR(")))")
      floor should be(-3)
    }

    it("should return to -3 floor when input is ))(") {
      val floor = Puzzle1.floorR(")())())")
      floor should be(-3)
    }
  }

